import {addAdmin} from '../../controllers/api/adminController';
import {addBusiness} from "../../controllers/api/businessController";
import {searchByCategory} from "../../controllers/api/searchController";

const router = express.Router();

router.post('/addAdmin', addAdmin);
router.get('/fetchCategory', fetchCategory);
router.post('/addCategory', addCategory);
router.post('/addBusiness', addBusiness);
router.get('/searchByCategory', searchByCategory);
export default router;

